mapper.select:
SELECT 

FROM dy_attention a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.client_id = #{clientId}


		AND a.domainname_id = #{domainnameId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dy_attention a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
DELETE FROM dy_attention
WHERE
	del_flag = #{DEL_FLAG_NORMAL}
AND client_id = #{clientId}
AND domainname_id = #{domainnameId}

mapper.select:
SELECT a.id AS "id", a.dyid AS "dyid", a.openid AS "openid", a.name AS "name", a.nickname AS "nickname", a.email AS "email", a.mobile AS "mobile", a.qq AS "qq", a.wx AS "wx", a.photo AS "photo", a.vip AS "vip", a.seal_flag AS "sealFlag", a.broker_id AS "brokerId", a.authentication_mark AS "authenticationMark", a.default_income_expense AS "defaultIncomeExpense", a.authentication_positive_image_url AS "authenticationPositiveImageUrl", a.authentication_negative_image_url AS "authenticationNegativeImageUrl", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag" FROM dy_attention b LEFT JOIN dy_client a ON a.id = b.client_id AND a.del_flag = 0
a.del_flag = 0 AND b.domainname_id = #{domainnameId}
ORDER BY b.create_date DESC
