mapper.select:
SELECT , b.name AS "dyDomainname.name" FROM dy_cash_flow a LEFT JOIN dy_domainname b ON a.domainname_id = b.id AND a.del_flag = #{DEL_FLAG_NORMAL} AND a.del_flag = #{DEL_FLAG_NORMAL}
a.client_id = #{clientId} AND a.operate NOT IN ('冻结','解冻')
ORDER BY a.operate_time DESC

SELECT , b.name AS "dyDomainname.name" FROM dy_cash_flow a LEFT JOIN dy_domainname b ON a.domainname_id = b.id AND a.del_flag = #{DEL_FLAG_NORMAL} AND a.del_flag = #{DEL_FLAG_NORMAL}
a.client_id = #{clientId} AND a.operate IN ('冻结','解冻','提现')
ORDER BY a.operate_time DESC

SELECT 

FROM dy_cash_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.client_id = #{clientId}


		AND a.domainname_id = #{domainnameId}


		AND a.operate = #{operate}


		AND a.operate_amount = #{operateAmount}


		AND a.operate_time = #{operateTime}


		AND a.confirm_result = #{confirmResult}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

	,d.name As "dyDomainname.name"
FROM dy_cash_flow a LEFT JOIN dy_domainname d ON a.domainname_id = d.id


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.operate not in ('冻结','解冻')

		AND a.client_id = #{clientId}


		AND a.operate_amount = #{operateAmount}


		AND a.operate_time = #{operateTime}


		AND a.confirm_result = #{confirmResult}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

