mapper.select:
SELECT 

FROM dy_domainname a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.id = #{id}


		AND a.client_id = #{clientId}


		AND a.begin_time = #{beginTime}


		AND a.end_time = #{endTime}


		AND a.deposit = #{deposit}


		AND a.description = #{description}


		AND a.status >= #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT DISTINCT


	,if(a.client_id = #{clientId} or b.id IS NOT NULL or c.id IS NOT NULL, '1', '2') as 'tmp'

FROM dy_domainname a

LEFT JOIN dy_attention b on b.domainname_id = a.id and b.client_id = #{clientId}
LEFT JOIN dy_bidhistory c on c.domainname_id = a.id and c.client_id = #{clientId}



	a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.name LIKE 
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


	AND a.id = #{id}
	AND a.status >= #{status}



	AND (a.end_time IS NULL OR to_days(a.end_time) - to_days(now()) >= 0)
	AND a.status = #{status}





	ORDER BY a.end_time asc, tmp asc


	ORDER BY a.end_time asc, tmp asc


	ORDER BY a.end_time asc

SELECT 

FROM dy_domainname a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
FROM dy_domainname a, dy_client c
c.id = a.client_id and a.del_flag = 0 and c.del_flag = 0
and c.id = #{clientId}
and a.status like #{status}
ORDER BY a.end_time ASC

mapper.update:
UPDATE dy_domainname SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

DELETE FROM dy_domainname
WHERE
	del_flag = '0'
AND client_id = #{clientId}
AND id = #{id}
AND status = '02'

UPDATE dy_domainname SET 
	update_date = NOW(),
	end_time = #{1}
WHERE id = #{0}
AND del_flag = 0

UPDATE dy_domainname SET 
	update_date = NOW(),
	status = #{1}
WHERE status = #{0}
AND wait_time <= NOW()
AND del_flag = 0

mapper.select:
SELECT d.id AS domainnameId, d.client_id As sellerId, b.client_id As buyerId, b.bid_amount As bidAmount, b.proxy_amount As proxyAmount FROM dy_domainname d ,dy_bidhistory b WHERE d.status = #{status}
and d.id = #{domainnameId}
AND d.end_time <= NOW() AND d.reserve_price = 0 AND d.del_flag = 0 AND b.del_flag = 0 AND d.id = b.domainname_id AND EXISTS ( SELECT domainname_id, bid_amount,client_id FROM ( SELECT k.* FROM (select * from dy_bidhistory where del_flag = '0' order by bid_amount desc ,proxy_amount desc) k GROUP BY k.domainname_id) t WHERE b.domainname_id = t.domainname_id AND b.bid_amount = t.bid_amount AND b.client_id = t.client_id)

SELECT
FROM dy_domainname a WHERE a.status = #{domainStatus03}
and a.id = #{domainnameId}
AND a.end_time <= NOW() AND a.del_flag = 0 AND not exists (select * from (select domainname_id from dy_bidhistory group by domainname_id)t where a.id = t.domainname_id )

SELECT d.id AS domainnameId, d.client_id As sellerId, b.client_id As buyerId, b.bid_amount As bidAmount, b.proxy_amount As proxyAmount FROM dy_domainname d ,dy_bidhistory b WHERE d.status = #{status}
and d.id = #{domainnameId}
AND d.end_time <= NOW() AND d.reserve_price > 0 AND d.del_flag = 0 AND b.del_flag = 0 AND d.id = b.domainname_id AND EXISTS ( SELECT domainname_id, bid_amount,client_id FROM ( SELECT k.* FROM (select * from dy_bidhistory where del_flag = '0' order by bid_amount desc ,proxy_amount desc) k GROUP BY k.domainname_id) t WHERE b.domainname_id = t.domainname_id AND b.bid_amount = t.bid_amount AND b.client_id = t.client_id AND d.reserve_price > t.bid_amount)

SELECT d.id AS domainnameId, d.client_id As sellerId, b.client_id As buyerId, b.bid_amount As bidAmount, b.proxy_amount As proxyAmount FROM dy_domainname d ,dy_bidhistory b WHERE d.status = #{status}
and d.id = #{domainnameId}
AND d.end_time <= NOW() AND d.reserve_price > 0 AND d.del_flag = 0 AND b.del_flag = 0 AND d.id = b.domainname_id AND EXISTS ( SELECT domainname_id, bid_amount,client_id FROM ( SELECT k.* FROM (select * from dy_bidhistory where del_flag = '0' order by bid_amount desc ,proxy_amount desc) k GROUP BY k.domainname_id) t WHERE b.domainname_id = t.domainname_id AND b.bid_amount = t.bid_amount AND b.client_id = t.client_id AND d.reserve_price < t.bid_amount)
