mapper.select:
SELECT 

FROM gb_sold a

WHERE a.id = #{id}

SELECT
FROM gb_sold a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM gb_sold a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE gb_sold SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
