mapper.select:
SELECT 

FROM gbj_buy a

WHERE a.id = #{id}

SELECT 

FROM gbj_buy a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.realname = #{realname}


		AND a.type_id = #{typeId}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.tag = #{tag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_buy a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_buy SET 
	front_del_flag = 1
WHERE id = #{id}

UPDATE gbj_buy SET 
	front_del_flag = 0
WHERE id = #{id}

UPDATE gbj_buy SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT b.id as "id", b.type_id as "type", b.create_date as "createDate", b.title as "title", b.up_counts as "upCounts" FROM gbj_buy b
b.del_flag = 0 ORDER BY b.update_date DESC
limit #{counts}

SELECT b.type_id as "type", b.create_date as "createDate", b.title as "title" FROM gbj_buy b
b.del_flag = 0 ORDER BY b.update_date DESC
limit #{counts}
