mapper.select:
SELECT 

FROM gbj_reward a

WHERE a.id = #{id}

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.realname = #{realname}


		AND a.type_id = #{typeId}


		AND a.price = #{price}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_reward SET 
	front_del_flag = 1
WHERE id = #{id}

UPDATE gbj_reward SET 
	front_del_flag = 0
WHERE id = #{id}

UPDATE gbj_reward SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT b.id as "id", b.type_id as "type", b.price AS "price", b.create_date as "createDate", b.title as "title", b.up_counts AS "upCounts" FROM gbj_reward b
b.del_flag = 0 ORDER BY b.update_date DESC
limit #{counts}

mapper.update:
UPDATE gbj_reward SET 	
	up_counts = up_counts + 1

WHERE id = #{id}
