mapper.select:
SELECT 

FROM gbj_reward a

WHERE a.id = #{id}

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_reward SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT b.reward_money as "rewardMoney", b.title as "title" FROM gbj_reward b
b.del_flag = 0 ORDER BY b.update_date DESC
limit #{counts}
