mapper.select:
SELECT 

FROM gbj_reward a

WHERE a.id = #{id}

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.realname = #{realname}


		AND a.type_id = #{typeId}


		AND a.price = #{price}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_reward a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_reward SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
