mapper.select:
SELECT 

FROM gbj_user_buy_comments_reply a

WHERE a.id = #{id}

SELECT 

FROM gbj_user_buy_comments_reply a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_user_buy_comments_reply a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_user_buy_comments_reply SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
