mapper.select:
SELECT 

FROM gbj_user a

WHERE a.id = #{id}

SELECT 

FROM gbj_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.username = #{username}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
