mapper.select:
SELECT 

FROM gbj_user_reward_comments a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.comment = #{comment}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_user_reward_comments a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_user_reward_comments SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
