mapper.select:
SELECT 

FROM gbj_user_sold_comments a

WHERE a.id = #{id}

SELECT 

FROM gbj_user_sold_comments a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gbj_user_sold_comments a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gbj_user_sold_comments SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
