SELECT d.id as "id", d.type_id as "typeId", d.mobile as "mobile", d.create_date as "createDate", d.title as "title", d.description as "description", d.up_counts as "upCounts" FROM gbj_sold d
d.del_flag = 0 AND d.front_del_flag = 1 ORDER BY d.update_date DESC
limit #{counts}
